import React from "react";

import {CrecheSliderdata} from '../data';

import {Swiper, SwiperSlide} from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/free-mode';
import '../slider.css';
import { FreeMode, Pagination } from "swiper";

 const CrecheSlider = () => { 
      return ( 
        <div className="container py-4 px-4 justify-center bg-white flex   ">
   <Swiper freeMode={true} grabCursor={true} modules={[FreeMode]} className="mySwiper" slidesPerView={1} spaceBetween={20}> 
      { CrecheSliderdata.map((slide,index) => {
        const {image,name,Rating,Description}=slide;
        return (
            
            <SwiperSlide key={index} className="p-0 overflow-hidden h-100 shadow  w-full  flex flex-col  my-4 rounded-lg " >
              
                <div className="overflow-hidden rounded p-0  ">
                <img src={image} alt="" />
                
                
                    {name}
                    <div className="mb-4 font-semibold ">{Rating}</div>
                    <p className=" max-w-[240px] ">{Description}</p>
                </div>

                <div className="bg-[#F6C9C7] w-full shadow-xl flex flex-col p-4 my-4 rounded-lg hover:scale-105 duration-300">
                        <img className="w-20 mx-auto mt-[-3rem]" src={one} alt="/" />
                        <h2 className="text-2xl font-bold text-center py-8">Creez un compte</h2>
                        <p className="text-center text-xl py-4 mx-8 "> 
                            Inscrivez-vous pour
                            pouvoir profiter pleinement
                            de toutes les fonctionnalités 
                            proposées par notre site 
                       </p>
                       <button className="bg-[#FB9B90] w-[200px] rounded-md font-medium my-6 mx-auto px-6 py-3 text-[#191A43] border border-[#ED6361]"> Se connecter </button>
                      </div>
             
            </SwiperSlide>
            
        );
      })}

   </Swiper>
   </div>
   );
      
 };

 export default CrecheSlider;
