import React from "react";
import pic from "../assets/pic1.svg";

const Filters = () => { 
    return ( 
<div className=" w-full text-[#0B0C38] bg-[#FBEDEC] py-16 px-4 ">
    <div className=" max-w-[1240px] mx-auto grid md:grid-cols-2">
          < img className=" w-[500px] mx-auto my-4" src={pic} alt="/" />
    <div className="flex flex-col justify-center "> 
        <h4 className="text-[#FB9B90] text-3xl font-bold font-concert"> LA CRECHE DA COTE</h4>
        <h1 className="md:text-4xl sm:text-3xl text-2xl font-bold py-2 font-inter ">Rechercher En Utilisant Nos Filtres</h1>
       <div>
       <button className="bg-[#FB9B90] w-[200px] rounded-md font-medium my-6 mx-auto md:mx-0 py-3 text-[#191A43] border border-[#ED6361] hover:scale-105 duration-300"> Nom de la crèche </button>
      <button className="bg-[#FB9B90] w-[200px] rounded-md font-medium my-6 mx-auto md:mx-0 py-3 text-[#191A43] border border-[#ED6361] hover:scale-105 duration-300"> Jours d’accueil </button>
      <button className="bg-[#FB9B90] w-[200px] rounded-md font-medium my-6 mx-auto md:mx-0 py-3 text-[#191A43] border border-[#ED6361] hover:scale-105 duration-300"> Privée </button>
      <button className="bg-[#FB9B90] w-[200px] rounded-md font-medium my-6 mx-auto md:mx-0 py-3 text-[#191A43] border border-[#ED6361] hover:scale-105 duration-300"> A proximité </button>
        </div> 
      
    </div>
   </div>
</div>
    )
}

export default Filters;
