import React from 'react';
import Desc from './Desc';
import { CrecheSliderdata } from '../../data';

const MyButton = ({ name }) => {
  const handleClick = () =>  { 

    const datacamp= CrecheSliderdata.map((data) => {
  
      return (
        <Desc key={data.key} nom={data.nom}  />
      )
    }) 
  
    
        return ( 
        <div className="w-full grid pio:grid-cols-2 ">
               {datacamp}
        </div>
              
  
            );
          };

  return (
    <button onClick={handleClick}>Say hello to {name}</button>
  );
};

export default MyButton;
