import React from "react";

import {CrecheSliderdata} from '../data';

import {Swiper, SwiperSlide} from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/free-mode';
import '../slider.css';
import { FreeMode, Pagination } from "swiper";

 const CrecheSlider = () => { 
      return ( 
        
   <Swiper freeMode={true} grabCursor={true} modules={[FreeMode]} className="mySwiper" slidesPerView={2} spaceBetween={20}> 
      { CrecheSliderdata.map((slide,index) => {
        const {image,name,Rating,Description}=slide;
        return (
            
            <SwiperSlide key={index} className="p-0 overflow-hidden h-100 w-full  flex flex-col  my-4 bg-white shadow-[0px_4.39131px_17.5652px_rgba(158,158,158,0.25)] rounded-[17.5652px] " >
              
                
                  
                        <img className=" mx-auto mt-3" src={image} alt="" /> 
                     
                
                    <div className="text-center text-2xl font-bold ">{name}</div>
                  <div className="text-center">{Rating}</div> 
                   <div className="text-center"> {Description}</div>
                

                
                        
                       
             
            </SwiperSlide>
            
        );
      })}

   </Swiper>
 
   );
      
 };

 export default CrecheSlider;
