import React from "react";

import {CrecheSliderdata} from '../data';

import {Swiper, SwiperSlide} from 'swiper/react';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/free-mode';
import '../slider.css';
import { FreeMode, Pagination } from "swiper";

 const CrecheSlider = () => { 
      return ( 
        <div className="container py-5 px-1 ">
   <Swiper freeMode={true} grabCursor={true} modules={[FreeMode]} className="mySwiper" slidesPerView={3} spaceBetween={25}> 
      { CrecheSliderdata.map((slide,index) => {
        const {image,name,Location,Description}=slide;
        return (
            
            <SwiperSlide key={index} className="p-0 overflow-hidden h-100   w-full  flex flex-col  my-4 bg-white shadow-[0px_4.39131px_17.5652px_rgba(158,158,158,0.25)] rounded-[17.5652px]  " >
              
                
                  
                        <img className=" mx-auto mt-3" src={image} alt="" /> 
                     
                
                    <div className="text-left pl-8 text-2xl font-bold py-2  ">{name}</div>
                  <div className="text-left pl-8  font-bold ">{Location}</div> 
                  <button className="bg-[#FB9B90] w-[170px] rounded-md font-bold my-6 mx-auto px-6 py-3 text-[#191A43] border border-[#ED6361]"> Voir plus </button>
                

                
                        
                       
             
            </SwiperSlide>
            
        );
      })}

   </Swiper>
   </div>
   );
      
 };

 export default CrecheSlider;
